import '../linkedIn.css';
import '../linkedInFeed.css';
import '../icomoon/style.css';
import file from '../images/file.png'
import download from '../images/download-cloud.png';
import PostHeader from './post_header';
import PostAuthor from './post_author';
import PostButtonsBar from './post_buttons';
import Column from './functions';

function PostText(props) {
    if (props.text && props.text.length < 301) {
        return (<span className="post_text margin-bottom_15">{props.text}</span>);
    } else if (props.text && props.text.length >= 301) {
        return (
            <Column>
                <span className="post_text margin-bottom_15">{props.text}</span>
                <a href="index.html" className="href_text blue_color uppercase">read more</a>
            </Column>
        );
    } else {
        return (<span/>);
    }
}

function PostFile(props) {
    return (
        <div className="post_file__block">
            <img scr={file} className="post_file__icon"/>
            <Column>
                <span className="font-size_14 font-weight_bolder">{props.name}</span>
                <span className="font-size_10 font-weight_lighter">{"PDF file, " + props.size + " kb"}</span>
            </Column>
            <img scr={download} className="download_image"/>
        </div>
    );
}

function Post(props) {
    return (<div className="post__block">
        <PostHeader
            text={props.header_text}
            hrefs={props.header_hrefs}
        />
        <PostAuthor
            avatar_src={props.avatar_src}
            author_name={props.author_name}
            author_status={props.author_status}
        />
        <PostText text={props.post_text}/>

        {props.images.map((object) => <img className="margin-bottom_15" src={object}/>)}

        {props.files.map((file) => <PostFile name={file.name}
                                             size={file.size}/>)}

        <PostButtonsBar thumbs_checked={props.thumbs_checked}
                        thumbs_amount={props.thumbs_amount}
                        messages_checked={props.messages_checked}
                        messages_amount={props.messages_amount}
        />
    </div>)
}

export default Post;
